import GuestListRoutingProtocol
import UIKit
import Models

protocol GuestListBusinessLogic {
    func fetchGuests()
}

class GuestListInteractor: GuestListBusinessLogic, GuestListDataStore {
    var presenter: GuestListPresentationLogic?
    var worker: GuestListWorkerProtocol?
    
    init(worker: GuestListWorkerProtocol = GuestListWorker()) {
        self.worker = worker
    }
    
    var guests: [Guest] = []
    
    var meetingId: Int?
    
    // MARK: Business Logic
    
    func fetchGuests() {
        guard let worker = worker else {
            return
        }
        Task {
            do {
                let guests = try await worker.fetchGuests(weddingID: meetingId ?? 1)
                self.guests = guests
                var lessIndexGuests: [Guest] = []
                var greaterIndexGuests: [Guest] = []
                for guest in guests {
                    if guest.user.id < GuestListModule.shared.guestListIndex {
                        lessIndexGuests.append(guest)
                    }
                    else {
                        greaterIndexGuests.append(guest)
                    }
                }
                self.presenter?.presentGuests(response: .init(guests: greaterIndexGuests + lessIndexGuests))
            }
            catch {
                // TODO: 에러처리
            }
        }
    }
}
