import UIKit
import SplashRoutingProtocol
import MeetingListScene
import LoginScene

public final class SplashRouter: SplashRoutingLogic, SplashDataPassing {
    public weak var viewController: UIViewController?
    public var dataStore: SplashDataStore?

    private var window: UIWindow? {
        if let scene = UIApplication.shared.connectedScenes.first as? UIWindowScene {
            return scene.windows.first
        }
        return nil
    }
    public init() {}
    
    // MARK: Routing
    
    public func removeFromParent() {
        viewController?.navigationController?.popViewController(animated: true)
    }

    public func routeToLoginViewController() {
        let loginViewController = LoginViewController()
        UIView.transition(
            with: self.window!,
            duration: 0.2,
            options: .transitionCrossDissolve,
            animations: {
                self.window?.rootViewController = loginViewController
            }, completion: nil)
    }

    public func routeToMeetingListViewController() {
        let meetingListViewController = MeetingListViewController()
        UIView.transition(
            with: self.window!,
            duration: 0.2,
            options: .transitionCrossDissolve,
            animations: {
                self.window?.rootViewController = meetingListViewController
            }, completion: nil)
    }
}
